    def setTextTurn(self, i):
        if i == 1:
            self.turnValue.setText("AI")
        else:
            self.turnValue.setText("Human")

    def activateChrono(self):
        self.flag_chrono = True
        self.timer = QtCore.QTimer(self)
        # adding action to timer
        #self.worker.buttonTab = self.array_button
        self.timer.timeout.connect(self.updateChrono)
        # update the timer every tenth second
        self.timer.start(100)

    def desactivateChrono(self):
        self.flag_chrono = False
        self.count = 0
        self.timer.stop()

    def updateChrono(self):
        if self.flag_chrono:
  
            # incrementing the counter
            self.count+= 1
  
        # getting text from count
        text = str(self.count / 10)
        self.timeValue.setText(text)

    def dialWinner(self, s):
        dlg = CustomDialog(s)
        if dlg.exec_():
            print("Success!")
            self.setupUi()
            self.worker.Start = np.zeros((self.worker.HEIGHT,self.worker.WIDTH))
            self.worker.buttonTab = self.array_button
            self.worker.reset()
        else:
            print("Cancel!")
            self.worker.QUIT = True
            del self.worker
            self.close()

